import  React, {useState, useEffect} from 'react';
import { Text, View ,Button , TextInput , FlatList , Image } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import axios from 'axios'; 
import { TouchableOpacity } from 'react-native';


function HomeScreen({navigation}) {

    const [data, setData] = useState([]);
    const [artist, SetArtiest] = useState("");
    const [pic , setPic] = useState("");
    const [album , setAlbum] = useState("");

    useEffect(() => {
        const khara = async () => {
            const khaara = await axios.get(' https://api.happi.dev/v1/music/artists/1990/albums/21830/tracks' , {
                params: {
                    apikey: 'f7b41dV3xOEEkSoW2ZqmCceGDfzpoEVW3zkOGVObzGhChN3puH436htA'
                }
            });
          setData(khaara.data.result.tracks);
          setAlbum(khaara.data.result.album);
          setPic(khaara.data.result.cover);
          SetArtiest(khaara.data.result.artist);

        }
       khara();
    },[]);

    return (
        <View>
           <TouchableOpacity onPress={() =>navigation.navigate('Lyrics',khaara.data)}>
           <FlatList
            keyExtractor={item => item.index}
            data={data}
            renderItem= { ({ item }) => {
                return (
                <View style={{backgroundColor:"lightgray" , marginBottom: 50}}>
                    <Image source={{uri:pic}} style = {{height: 400, resizeMode : 'stretch', margin: 5 }} />
                    <Text> {` Song NAME :${item.track}`} </Text>
                    <Text> {` Artist NAME :${artist}`} </Text>
                    <Text> {` Artist NAME :${album}`} </Text>

                    <Text>~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~</Text>
                </View>
                 ) }}
            />
           </TouchableOpacity>
        </View>
  );
}

function Albums() {
    const [data, setData] = useState([]);

    useEffect(() => {
        const khara = async () => {
            const khaara = await axios.get('https://api.happi.dev/v1/music/artists/1404/albums' , {
                params: {
                    apikey: 'f7b41dV3xOEEkSoW2ZqmCceGDfzpoEVW3zkOGVObzGhChN3puH436htA'
                }
            });
          setData(khaara.data.result.albums);
        }
       khara();
    },[]);

  return (
   <View>
       <FlatList 
        data= {data}
        keyExtractor={ item => item.index}
        renderItem = { ({item}) => {
            return (
                <View>
                <Image source={{uri:item.cover}} style = {{height: 400, resizeMode : 'stretch', margin: 5 }} />
                <Text> {` Album NAME :${item.album}`} </Text>
            </View>
            )
        }}
        />

   </View>
  );
}


function SettingsScreen() {
    const [data, setData] = useState([]);

    useEffect(() => {
        const khara = async () => {
            const khaara = await axios.get('https://api.happi.dev/v1/music/artists' , {
                params: {
                    page: 0,
                    apikey: 'f7b41dV3xOEEkSoW2ZqmCceGDfzpoEVW3zkOGVObzGhChN3puH436htA'
                }
            });
          setData(khaara.data.result);
        }

       khara();
    },[]);

   return (
        <View>

            <FlatList 
                data= {data}
                keyExtractor={ item => item.index}
                renderItem = { ({item}) => {
                  return (
                    <View style={{marginBottom:30 , backgroundColor: "lightgray"}}>
                    <Image source={{uri:item.cover}} style = {{height: 400, resizeMode : 'stretch', margin: 5 }} />
                    <Text> {` Artist NAME :${item.artist}`} </Text>
                    <Text>~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~</Text>
                    </View>
                  )
                }}
            />
        </View>
    );
}

function Search() {

    const [search, setSearch] = useState([]);
    const [text, setText] = useState("");

   useEffect(() => {
    const khara = async () => {
        const khaara = await axios.get('https://api.happi.dev/v1/music' , {
            params: {
                q: text ,
                limit: 30 ,
                apikey: 'f7b41dV3xOEEkSoW2ZqmCceGDfzpoEVW3zkOGVObzGhChN3puH436htA'
            }
        });
        setSearch(khaara.data.result);
    }
    khara();
   }, [text])

    return (
      <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
          <TextInput style={{borderBottomColor:"gray"}} onChangeText={(text)=> setText(text)} title="find your faviote song"  placeholder="Search for your faviote song"/>
          <Text>Your Results</Text>
          <FlatList
            keyExtractor={item => item.index}
            data={search}
            renderItem= { ({ item }) => {
                return (
                <View>
                    <Image source={{uri:item.cover}} style = {{height: 400, resizeMode : 'stretch', margin: 5 }} />
                    <Text> {` Artist NAME :${item.artist}`} </Text>
                    <Text> {` Album NAME :${item.album}`}</Text>
                    <Text> {` Track NAME :${item.track}`}</Text>
                    <Text>~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~</Text>
                </View>
                 ) }}
            />
      </View>
    );
  }

const Tab = createBottomTabNavigator();

export default function App() {
  return (
    <NavigationContainer>
      <Tab.Navigator>
        <Tab.Screen name="New Songs" component={HomeScreen} />
        <Tab.Screen name="Artists" component={SettingsScreen} />
        <Tab.Screen name="Top Albums" component={Albums} />
        <Tab.Screen name="Search" component={Search} />
      </Tab.Navigator>
    </NavigationContainer>
  );
}
